import React, { Component } from 'react'
import ConfigServerService from '../services/ConfigServerService'
import Dropdown from './Dropdown'
import { createRoot } from "react-dom/client";

class ConfigServerComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                configs: []
        }
        this.addProperty = this.addProperty.bind(this);
        this.editProperty = this.editProperty.bind(this);
        this.deleteProperty = this.deleteProperty.bind(this);
    }

    deleteProperty(id){
        ConfigServerService.deleteProperty(id).then( res => {
            this.setState({configs: 
                this.state.configs.filter(config => config.id !== id)});
        });
    }
    viewProperty(id){
        this.props.history.push(`/view-property/${id}`);
    }
    editProperty(id){
        this.props.history.push(`/add-property/${id}`);
    }
    componentDidMount(){
        ConfigServerService.getConfigs().then((res) => {
            if(res.data==null)
            {
                this.props.history.push('/add-property/_add');
            }
            this.setState({ configs: res.data});
        });
    }

    addProperty(){
        this.props.history.push('/add-property/_add');
    }

    render() {

        const svc_options = [
            { value: "Svc1", label: "Svc1" },
            { value: "Svc2", label: "Svc2" },
            { value: "Svc3", label: "Svc3" },
            { value: "Svc4", label: "Svc4" },

          ];
          const env_options = [
            { value: "Dev", label: "Dev" },
            { value: "Test", label: "Test" },
            { value: "Perf", label: "Perf" },
            { value: "Prod", label: "Prod" },

          ];
        return (



            <div>
                 <h2 className="text-center">
                     Config Server Properties</h2>
                 <div className = "row">
                    <button className="btn btn-primary"
                     onClick={this.addProperty}> Add Property</button>
                 </div>
                 <br></br>
                 <div className="App">
                    <Dropdown
                        isSearchable
                        isMulti
                        placeHolder="Select..."
                        options={svc_options}
                        onChange={(value) => console.log(value)}
                    />

                    <Dropdown
                        isSearchable
                        isMulti
                        placeHolder="Select..."
                        options={env_options}
                        onChange={(value) => console.log(value)}
                    />
                </div>


                 <div className = "row">
                        <table className 
                        = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> ID</th>
                                    <th> Service Name</th>
                                    <th> Env</th>
                                    <th> Label </th>
                                    <th> Property</th>
                                    <th> Value</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.configs.map(
                                        config => 
                                        <tr key = {config.id}>
                                   <td> {config.id} </td>         
                                   <td> {config.serviceName} </td>   
                                   <td> {config.profile}</td>
                                   <td> {config.lable}</td>
                                   <td> {config.property}</td>
                                   <td> {config.value}</td>

                                             <td>
                      <button onClick={ () => 
                          this.editConfig(config.id)} 
                               className="btn btn-info">Update 
                                 </button>
                       <button style={{marginLeft: "10px"}}
                          onClick={ () => this.deleteConfig(config.id)} 
                             className="btn btn-danger">Delete 
                                 </button>
                       <button style={{marginLeft: "10px"}} 
                           onClick={ () => this.viewProperty(config.id)}
                              className="btn btn-info">View 
                                  </button>
                                    </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>
                 </div>
            </div>
        )
    }
}

export default ConfigServerComponent
